import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { Container } from '@mui/material';
import Dashboard from './Dashboard';
import AddBug from './AddBug';
import BugList from './BugList';
import Login from './Login';
import Register from './Register';
import Home from './Home';
import Navbar from './Navbar';

function App() {
  const [bugs, setBugs] = useState([]);
  const [bugName, setBugName] = useState('');
  const [bugDescription, setBugDescription] = useState('');
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    if (token) {
      fetch(`${process.env.REACT_APP_API_URL}/api/bugs`, {
        headers: { 'Authorization': `Bearer ${token}` }
      })
        .then(response => response.json())
        .then(data => setBugs(data))
        .catch(error => console.error('Error fetching bugs:', error));
    }
  }, [token]);

  const handleAddBug = async () => {
    const response = await fetch(`${process.env.REACT_APP_API_URL}/api/bugs`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({ name: bugName, description: bugDescription })
    });
    const newBug = await response.json();
    setBugs([...bugs, newBug]);
    setBugName('');
    setBugDescription('');
  };

  const handleResolveBug = async (id) => {
    await fetch(`${process.env.REACT_APP_API_URL}/api/bugs/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({ resolved: true })
    });
    setBugs(bugs.map(bug => bug.id === id ? { ...bug, resolved: true } : bug));
  };

  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('token');
  };

  return (
    <Router>
      <Navbar token={token} handleLogout={handleLogout} />
      <Container component="main" sx={{ marginTop: 4, padding: 2 }}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login setToken={(token) => { setToken(token); localStorage.setItem('token', token); }} />} />
          <Route path="/register" element={<Register setToken={(token) => { setToken(token); localStorage.setItem('token', token); }} />} />
          {token ? (
            <>
              <Route path="/dashboard" element={
                <Dashboard
                  bugs={bugs}
                  handleResolveBug={handleResolveBug}
                />
              } />
              <Route path="/addbug" element={
                <AddBug
                  bugName={bugName}
                  setBugName={setBugName}
                  bugDescription={bugDescription}
                  setBugDescription={setBugDescription}
                  handleAddBug={handleAddBug}
                />
              } />
              <Route path="/bugs" element={<BugList bugs={bugs} />} />
            </>
          ) : (
            <Route path="*" element={<Navigate to="/login" />} />
          )}
        </Routes>
      </Container>
    </Router>
  );
}

export default App;
