import React, { useState, useEffect, createContext, useContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { Container } from '@mui/material';
import AddBug from './AddBug';
import Login from './forms/Login';
import Register from './forms/Register';
import Home from './Home';
import Navbar from './components/Navbar';
import BugList from './BugsList';

const AuthContext = createContext(null);

const useAuth = () => {
  const [token, setToken] = useState(localStorage.getItem('token'));

  const login = (newToken) => {
    localStorage.setItem('token', newToken);
    setToken(newToken);
  };

  const logout = () => {
    localStorage.removeItem('token');
    setToken(null);
  };

  return { token, login, logout };
};

function App() {
  const { token, login, logout } = useAuth();
  const [bugs, setBugs] = useState([]);
  const socket = new WebSocket('ws://localhost:8080');

  useEffect(() => {
    if (token) {
      socket.onopen = () => {
        console.log('WebSocket Connected');
      };

      socket.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 'init' || data.type === 'update') {
          setBugs(data.bugs);
        }
      };

      socket.onerror = (error) => {
        console.error('WebSocket Error:', error);
      };

      return () => socket.close();
    }
  }, [token]);

  const handleAddBug = (bugName, bugDescription) => {
    socket.send(JSON.stringify({ type: 'addBug', name: bugName, description: bugDescription }));
  };

  const handleResolveBug = (id) => {
    socket.send(JSON.stringify({ type: 'resolveBug', id }));
  };

  return (
    <AuthContext.Provider value={{ token, login, logout }}>
      <Router>
        <Navbar />
        <Container component="main" sx={{ marginTop: 4, padding: 2 }}>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login setToken={login} />} />
            <Route path="/register" element={<Register setToken={login} />} />
            {token ? (
              <>
                <Route path="/addbug" element={
                  <AddBug handleAddBug={handleAddBug} />
                } />
                <Route path="/bugs" element={<BugList bugs={bugs} />} />
              </>
            ) : (
              <Route path="*" element={<Navigate to="/login" />} />
            )}
          </Routes>
        </Container>
      </Router>
    </AuthContext.Provider>
  );
}

export const useAuthContext = () => useContext(AuthContext);

export default App;
