import React, { useState, useEffect } from 'react';
import { Container, Typography, Paper, List, ListItem, ListItemText, Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, TextField } from '@mui/material';

function BugList({ handleDeleteBug, handleEditBug }) {
    const [open, setOpen] = useState(false);
    const [currentBug, setCurrentBug] = useState({ id: '', name: '', description: '' });
    const [bugs, setBugs] = useState([]);
    const [error, setError] = useState(null);

    const fetchBugs = async () => {
        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/bugs`, {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` },
                method: 'GET'
            });
            console.log('Fetch response status:', response.status);

            if (response.status === 401) {
                throw new Error('Unauthorized');
            }

            if (!response.ok) {
                const errorText = await response.text();
                throw new Error(errorText || 'Failed to fetch bugs');
            }

            const data = await response.json();
            setBugs(data);
        } catch (error) {
            console.error('Error fetching bugs:', error);
            setError(error.message);
        }
    };

    useEffect(() => {
        fetchBugs();
    }, []);

    const handleClickOpen = (bug) => {
        setCurrentBug(bug);
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleSave = async () => {
        await handleEditBug(currentBug);
        fetchBugs(); // Re-fetch the bugs after editing
        handleClose();
    };

    const handleDelete = async (id) => {
        await handleDeleteBug(id);
        fetchBugs(); // Re-fetch the bugs after deleting
    };

    return (
        <Container maxWidth="md">
            <Paper sx={{ padding: 2 }}>
                <Typography variant="h4" gutterBottom>All Bugs</Typography>
                {error && <Typography color="error">{error}</Typography>}
                <List>
                    {bugs.map(bug => (
                        <ListItem key={bug.id} divider>
                            <ListItemText primary={bug.name} secondary={bug.description} />
                            <Button variant="contained" color="primary" sx={{ margin: 1 }} onClick={() => handleClickOpen(bug)}>Edit</Button>
                            <Button variant="contained" color="secondary" sx={{ margin: 1 }} onClick={() => handleDelete(bug.id)}>Delete</Button>
                        </ListItem>
                    ))}
                </List>
                <Dialog open={open} onClose={handleClose}>
                    <DialogTitle>Edit Bug</DialogTitle>
                    <DialogContent>
                        <DialogContentText>
                            Update the bug details below.
                        </DialogContentText>
                        <TextField
                            autoFocus
                            margin="dense"
                            label="Bug Name"
                            type="text"
                            fullWidth
                            value={currentBug.name}
                            onChange={(e) => setCurrentBug({ ...currentBug, name: e.target.value })}
                        />
                        <TextField
                            margin="dense"
                            label="Bug Description"
                            type="text"
                            fullWidth
                            value={currentBug.description}
                            onChange={(e) => setCurrentBug({ ...currentBug, description: e.target.value })}
                        />
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleClose} color="primary">Cancel</Button>
                        <Button onClick={handleSave} color="primary">Save</Button>
                    </DialogActions>
                </Dialog>
            </Paper>
        </Container>
    );
}

export default BugList;
