  const [bugName, setBugName] = useState('');
  const [bugDescription, setBugDescription] = useState('');
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    if (token) {
      socket.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 'init' || data.type === 'update') {
          setBugs(data.bugs);
        }
      };
    }
  }, [token]);

  const handleAddBug = () => {
    socket.send(JSON.stringify({ type: 'addBug', name: bugName, description: bugDescription }));
    setBugName('');
    setBugDescription('');
  };

  const handleResolveBug = (id) => {
    socket.send(JSON.stringify({ type: 'resolveBug', id }));
  };

  const handleDeleteBug = (id) => {
    socket.send(JSON.stringify({ type: 'resolveBug', id }));
  };

  const handleEditBug = (bug) => {
    socket.send(JSON.stringify({ type: 'editBug', id: bug.id, name: bug.name, description: bug.description }));
  };

  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('token');
  };

